import React, { Component } from "react"

class PhoneInfo extends Component {
    static defaultProps = {
        info : {
            name: '이름',
            phone: '010-0000-0000',
            id: 0
        }
    }

    _handleRemove = () => {
        const { info, onRemove } = this.props;
        onRemove(info.id);
    }

    render() {
        const style = {
           border: '1px solid black',
           padding: '8px',
           margin: '8px'
        };

        const {
            id, name, phone
        } = this.props.info;

        return (
            <div style={style}>
                <div><b>{name}</b></div>
                <div>{phone}</div>
                <div>{id}</div>
                <button onClick={this._handleRemove}>삭제</button>
            </div>
        )
    }
}

export default PhoneInfo;
